import type { UpdateDownloadedEvent } from 'electron-updater';

export enum AutoUpdateDialogStates {
  AVAILABLE = 'available',
  NOT_AVAILABLE = 'not-available',
  QUIT_GAME = 'quit-game',
  ERROR = 'error',
}

export type AutoUpdateDialogState = `${AutoUpdateDialogStates}`;

export type Update = {
  updateInfo: UpdateDownloadedEvent;
  state: AutoUpdateDialogState;
};
