import { configuration } from '../..';

interface Identity {
  accountName?: string;
  heapAccountId?: string;
  trackingMetricsId?: string;
}

export type PlatformEnvs = keyof typeof configuration.env;

export interface GlobalConfigData {
  configuration: object;
  launcherVersion: string;
  patcherVersion: string;
  osMetMinimumRequirements: boolean;
  cpuMetRequirements: boolean;
  appPath: string;
  identity: Identity | null;
  environment: PlatformEnvs;
}
