import { GameChannelData } from './library';

export interface NetworkState {
  mesh: {
    channels: {
      [key: string]: {
        endpoint?: string;
        transport_security?: boolean;
      };
    };
  };
}

export interface GlobalSettings {
  hostname: string;
  port: number;
  servicesEndpoint: string;
  network: NetworkState | object;
  executable: string;
  launchOptions: string;
  eacSandbox: boolean;
  installDir: string;
  libraryFolder: string;
}

export interface SCSettings extends GlobalSettings, GameChannelData {}
export interface SQ42Settings extends GlobalSettings, GameChannelData {}

export type GameSettingsState = SCSettings | SQ42Settings;

// For now, we only store "libraryFolder" in the Electron store.
export type StoredGameSettingsState = Pick<GlobalSettings, 'libraryFolder'> & GameChannelData;

export interface SuccessMessageState {
  id: string;
  message: string;
}
