import { NetworkState, StoredGameSettingsState } from './settings';

export type LauncherSchemaType = {
  application: {
    volume: number;
    version: string;
    quitOnWindowClose: boolean;
    download: {
      concurrentTransfers: number;
      maximumDownloadBandwidth: number;
    };
    window: {
      large: {
        bounds: {
          x: number;
          y: number;
          width: number;
          height: number;
        } | null;
      },
      small: {
        bounds: {
          x: number;
          y: number;
          width: number;
          height: number;
        } | null;
      }
    };
  };
  identity: {
    username: string;
    privileged: boolean;
    heapAccountId: string;
    trackingMetricsId: string | null;
  } | null;
  session: {
    displayName: string;
    nickname: string;
    key: string;
    value: string;
    cookie: string;
    expires: number;
  } | null;
  device: {
    cookie: string;
    key: string;
    value: string;
  };
  library: {
    libraryFolder: string,
    installed: {
      gameId: string;
      gameName: string;
      channels: {
        id: string;
        name: string;
        version: number;
        versionLabel: string;
        platformId: string;
        servicesEndpoint: string;
        nid: string;
        network: NetworkState | null;
        status?: string;
      }[];
    }[];
    defaults: {
      platformId: string;
      gameId: string;
      gameName: string;
      channelId: string;
      channelName: string;
    }[];
    settings: StoredGameSettingsState[];
  };
};
